
//登入註冊切換

    var onclick_signup = document.querySelector(".button_signup");
    var onclick_login = document.querySelector(".button_login");
    var login = document.getElementById("content_login");
    var signup = document.getElementById("content_signup");
    var form_signup = document.querySelector(".form_signup");
    var form_login = document.querySelector(".form_login");

    var test = document.querySelector(".test");
    test.onclick = function(){
        form_signup[4].type = "text";
    }

    onclick_signup.onclick = function(){
        if (signup.style.display === "none"){
            login.style.display = "none";
            signup.style.display = "block";//signup出現
            for(var i = 0 ; i < form_signup.length ; i++){
                form_signup[i].value =""; 
            }

        }
    }
    onclick_login.onclick = function(){
        if (login.style.display === "none"){
            signup.style.display = "none";
            login.style.display = "block";//login出現
            for(var i = 0 ; i < form_login.length ; i++){
                form_login[i].value =""; 
            }
        }
    }

//密碼眼睛

    var checkEye = document.querySelector(".fa-eye");
    var floatingPassword = document.querySelector(".password");
            checkEye.addEventListener("click", function(e){
            if(e.target.classList.contains('fa-eye')){
            //換class 病患 type
                e.target.classList.remove('fa-eye');
                e.target.classList.add('fa-eye-slash');
                floatingPassword.setAttribute('type','text');
            }
            checkEye.addEventListener('mouseleave', () => {
                floatingPassword.setAttribute('type','password');
                e.target.classList.remove('fa-eye-slash');
                e.target.classList.add('fa-eye')
              } ,false);
            
        });
